Ext.define('MyApp.view.Prediction', {
    extend: 'Ext.Panel',

    config: {
        id: 'predictionPanel',
        width: 326,
        items: [
            {
                xtype: 'spacefilling',
                height: 300,
                width: 320,
                series: [
                    {
                        type: 'gauge',
                        axes: [
                            {
                                type: 'gauge',
                                position: 'gauge',
                                minimum: 0,
                                maximum: 100,
                                steps: 10,
                                margin: 10
                            }
                        ],
                        subStyle: {
                            fillStyle: [
                                '#115fa6',
                                'lightgrey'
                            ]
                        },
                        title: 'Chance of Qualifying (%)',
                        donut: 80,
                        value: 0
                    }
                ]
            },
            {
                xtype: 'selectfield',
                itemId: 'myselectfield1',
                label: 'Select Team',
                labelWidth: '35%',
                displayField: 'teamName',
                store: 'pointTableStore',
                valueField: 'teamName'
            }
        ],
        listeners: [
            {
                fn: 'onMyselectfield1Change',
                event: 'change',
                delegate: '#myselectfield1'
            }
        ]
    },

    onMyselectfield1Change: function(selectfield, newValue, oldValue, options) {
        selectfield.getParent().setValueForSeries(newValue);
    },

    loadData: function(records) {
        var selectedTeam=this.getItems().items[1].getValue();
        this.records=records;
        this.setValueForSeries(selectedTeam);
    },

    setValueForSeries: function(teamName) {
        var series=this.getItems().items[0].getSeries()[0];
        Ext.each(this.records,function(record) {    
            if(record.get('teamName')===teamName) {
                var chances=record.get('chances');    
                var colors=['#F49D10', '#ddd'];
                if(chances>95) {
                    colors[0]='#0B610B';
                }
                else if(chances>90) {
                    colors[0]='#74DF00';
                }else if(chances>80) {
                    colors[0]='#FFBF00';
                }else if(chances>70) {
                    colors[0]='#E2A9F3';
                }else if(chances>60) {
                    colors[0]='#F7BE81';
                }else if(chances>40) {
                    colors[0]='#82B525';
                }else if(chances>10) {
                    colors[0]='#DF0101';
                }else {
                    colors[0]='#000000';
                }            
                series.setValue(chances);
                series.setColors(colors);
            }
        });
    }

});